$().ready(function () {
    $("video.avid").each(function () {
        $(this).data("url") && $(this).attr("controlsList", "nodownload").html('<source src="//cdn.ahacdu.com' + $(this).data("url") + '.mp4"><source src="//cdn.ahacdu.com' + $(this).data("url") + '.webm">')
    })
});

function cancelEvent(e) {
    try {
        e && (e.returnValue = defaults.get_locale_var("exit_text"), e.cancelBubble = !0, e.stopPropagation && e.stopPropagation(), e.preventDefault && e.preventDefault())
    } catch (e) {}
    return defaults.get_locale_var("exit_text")
}

function RemoveUnload() {
    window.onbeforeunload = null
}

function showLoader() {
    var e = document.createElement("div");
    e.id = "loader-block", e.className = "loader-block", document.getElementsByTagName("body")[0].appendChild(e);
    e = {
        position: "fixed",
        top: 0,
        left: 0,
        "z-index": 9999,
        width: "100%",
        height: "100%",
        background: "url(/!common_files/images/loader.gif) center center rgba(0,0,0,0.5) no-repeat"
    };
    $("#loader-block").css(e), $("#loader-block").animate({
        opacity: 0
    }, 2e4, function () {
        hideLoader()
    })
}

function hideLoader() {
    $("#loader-block").remove()
}
